import React from 'react';
import {useState, useEffect} from 'react';
import $ from 'jquery';
//with jQuery only !

const ThirdComponent = () => {
    const {context} = useState();

    onClickBtn = () => {
        console.log("ok btn")
        $('.button').click(function() {
            $('p').css('color', 'yellow')
        })
    };

    useEffect(() => {
        onClickBtn();
    }, [])


    return (
        <div>
            <h2>
                ThirdComponent
            </h2>
            <div>
                <h3 id="myDomEl">
                    jQuery
                </h3>
                <button type='button' onClick={onClickBtn()}>
                    Click
                </button>
            </div>

        </div>
    );
}

export default ThirdComponent;
