import React from 'react';

//Simple toggle to change gender for example a form.


export default class FirstComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            gender: false,
            name: ""
        };
        this.handleName = this.handleName.bind(this);
    }

    handleClick = (e) => {
        this.setState({gender: !this.state.gender});
    };

    handleChange = (e) => {
        this.setState({name: e.target.value});
        //console.log(e.target.value)
    };

    handleName = () => {

        //console.log("You've clicked handleName")
    };

    render() {
        const {gender, name} = this.state;

        return (
            <>

                <div className="gender--class">
                    <h4 value={gender}>
                        Gender: {gender ? "Madam" : "Mister"}
                    </h4>

                    <button type="button" onClick={(e) => this.handleClick(e)}>
                        Change Gender
                    </button>
                </div>

                <div className="target--class">
                    <h4>
                        Enter your name :
                    </h4>

                    <input value={name} onChange={(e) => this.handleChange(e)} />

                    <button type="button" onClick={() => this.handleName()}>
                        Enter
                    </button>
                </div>

                <div>
                    <h4 value={name}>
                        Verify your name : {name}
                    </h4>
                </div>

            </>

        );
    }
}
