import React from 'react';

//Simple toggle to change gender for example a form.


export default class FirstComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            gender: false,
            name: ""
        };
        //this.handleChange = this.handleChange.bind(this);
    }

    handleClick = (e) => {
        this.setState({gender: !this.state.gender});
    };

    handleChange = (e) => {
        console.log(e.target.value)
    };

    render() {
        const {gender, name} = this.state;

        return (
            <div>

                <div className="gender--class">
                    <h4 value={gender}>
                        Gender: {gender ? "Madam" : "Mister"}
                    </h4>

                    <button type="button" onClick={(e) => this.handleClick(e)}>
                        Change Gender
                    </button>
                </div>

                <div className="target--class">
                    <h4 value={name} handleChange={(e) => this.handleChange(e)}>
                        Enter your name : {name}
                    </h4>

                    <button type="button" onClick={(e) => this.handleName(e)}>
                        Enter
                    </button>
                </div>

            </div>

        );
    }
}
