import React from 'react';
import $ from 'jquery';
//with jQuery only !

const ThirdComponent = () => {

    const onClickBtn = () => {
        console.log("ok btn")
        var myDomEl = this.state.context.find('#myDomEl');
        myDomEl.css('background-color', 'green');
    };


    return (
        <div>
            <h2>
                ThirdComponent
            </h2>
            <div>
                <h3 id="myDomEl">
                    jQuery
                </h3>
                <button type='button' onClick={onClickBtn}>
                    Click
                </button>
            </div>

        </div>
    );
}

export default ThirdComponent;
