import React from 'react';

//Simple toggle to change gender for example a form.


export default class FirstComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            rememberMe: false,
            gender: false,
            name: "",
            passwd: ""
        };
        this.handleName = this.handleName.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
        this.Remove = this.Remove.bind(this);
    }

    componentDidMount() {
        const rememberMe = localStorage.getItem('rememberMe') === 'true';
        const name = rememberMe ? localStorage.getItem('Name') : '';
        const pwd = rememberMe ? localStorage.getItem('Password') : '';
        this.setState({ rememberMe, name, pwd });
    };

    handleClick = (e) => {
        this.setState({gender: !this.state.gender});
    };

    handleChangeName = (e) => {
        this.setState({name: e.target.value});
        //console.log(e.target.value)
    };

    handleChange = (event) => {
        const input = event.target;
        this.setState({
            [input.name]: input.type === 'checkbox' ? input.checked : input.value
        })
    };

    handleName = () => {
        console.log("You've clicked handleName")
    };

    handleFormSubmit = () => {
        const { rememberMe, name, pwd } = this.state;
        localStorage.setItem('rememberMe', rememberMe);
        localStorage.setItem('Name', name);
        localStorage.setItem('Password', pwd);
    };

    Remove = () => {
        localStorage.removeItem('rememberMe')
        localStorage.removeItem('Name');
        localStorage.removeItem('Password');
    };

    render() {
        const {gender, name} = this.state;

        return (
            <>

                <div className="gender--class">
                    <h4 value={gender}>
                        Gender: {gender ? "Madam" : "Mister"}
                    </h4>

                    <button type="button" onClick={(e) => this.handleClick(e)}>
                        Change Gender
                    </button>
                </div>

                <div className="target--class">
                    <h4>
                        Enter your name :
                    </h4>

                    <input value={name} onChange={(e) => this.handleChangeName(e)} />

                    <button type="button" onClick={() => this.handleName()}>
                        Enter
                    </button>
                </div>

                <div className="target--class">
                    <h4 value={name}>
                        Verify your name : {name}
                    </h4>

                </div>


                <div>
                    <label>
                        <h4>Password:</h4>
                        <input
                            name='password'
                            type="password"
                            placeholder="Password"
                            value={this.state.pwd}
                            onChange={(e) => this.setState({pwd: e.target.value})}
                            required />
                    </label>
                        {this.state.pwd}

                </div>


                <div>

                    <label className='lbl--input'>
                        <input
                            name="rememberMe"
                            type="checkbox"
                            checked={this.state.rememberMe}
                            onChange={this.handleChange} />
                        <h5>
                            Remember me
                        </h5>
                    </label>

                    <p>Name: {localStorage.getItem('Name')}</p>
                    <p>Password: {localStorage.getItem('Password')}</p>

                    <button type="submit" className='btn--submit'>Sign In</button>

                    <div>
                        <button
                            onClick={this.Remove}
                            className='btn--onClick'
                        >
                            Remove
                        </button>

                    </div>

                </div>
            </>

        );
    }
}
